// src/components/ChampionPerformance.js
import React, { useEffect, useState } from 'react';
import { getChampionPerformance } from '../services/api';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  CircularProgress,
  Avatar,
  Box,
} from '@mui/material';

function ChampionPerformance({ puuid }) {
  const [championPerformance, setChampionPerformance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPerformanceData = async () => {
      const data = await getChampionPerformance(puuid);
      if (!data.error) {
        setChampionPerformance(data.championPerformance);
      }
      setLoading(false);
    };
    fetchPerformanceData();
  }, [puuid]);

  if (loading) return <CircularProgress />;

  return (
    <TableContainer component={Paper} sx={{ mt: 4, borderRadius: 3, boxShadow: 4 }}>
      <Typography variant="h6" component="div" sx={{ p: 2, textAlign: 'center', fontWeight: 'bold' }}>
        Champion Performance
      </Typography>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Champion</TableCell>
            <TableCell align="center">Games Played</TableCell>
            <TableCell align="center">Win Rate (%)</TableCell>
            <TableCell align="center">Average KDA</TableCell>
            <TableCell align="center">CS/Min</TableCell>
            <TableCell align="center">Gold Earned</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {championPerformance.map((champ) => (
            <TableRow key={champ.champion} sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
              <TableCell>
                <Box display="flex" alignItems="center">
                  <Avatar
                    src={`https://ddragon.leagueoflegends.com/cdn/14.21.1/img/champion/${champ.champion}.png`}
                    alt={champ.champion}
                    sx={{ width: 40, height: 40, mr: 2 }}
                  />
                  <Typography variant="body1" fontWeight="bold" color="text.primary">
                    {champ.champion}
                  </Typography>
                </Box>
              </TableCell>
              <TableCell align="center">{champ.games}</TableCell>
              <TableCell align="center" sx={{ color: champ.win_rate >= 50 ? '#4caf50' : '#f44336', fontWeight: 'bold' }}>
                {champ.win_rate}%
              </TableCell>
              <TableCell align="center" sx={{ color: champ.average_kda.split(':')[0] >= 3 ? '#4caf50' : '#f44336', fontWeight: 'bold' }}>
                {champ.average_kda}
              </TableCell>
              <TableCell align="center">{champ.average_cs_per_min.toFixed(1)}</TableCell>
              <TableCell align="center">{champ.average_gold}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default ChampionPerformance;
