import React, { useState, useEffect } from 'react';
import { Container, Box, Typography, Grid, Card } from '@mui/material';
import { getAccountData, getSummonerData, getMatchHistoryWithWinRate, getVersion } from './services/api';
import SummonerForm from './components/SummonerForm';
import SummonerProfile from './components/SummonerProfile';
import MatchList from './components/MatchList';
import LoadMoreMatches from './components/LoadMoreMatches';
import ChampionPerformance from './components/ChampionPerformance';

function App() {
  const [summoner, setSummoner] = useState(null);
  const [matches, setMatches] = useState([]);
  const [version, setVersion] = useState('');
  const [wins, setWins] = useState(0);
  const [losses, setLosses] = useState(0);
  const [winRate, setWinRate] = useState(0);

  const [limit] = useState(10);

  useEffect(() => {
    const fetchVersion = async () => {
      const versionData = await getVersion();
      if (versionData) {
        setVersion(versionData.version);
      }
    };
    fetchVersion();
  }, []);

  const handleAccountDataFetched = async (data) => {
    const { puuid, gameName, tagLine } = data;
    const summonerData = await getSummonerData(puuid);
    setSummoner({ ...data, ...summonerData });
    setMatches([]);
  };

  return (
    <Box sx={{ bgcolor: '#1a1c23', minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      <Container
        maxWidth="md"
        sx={{
          color: '#f0f0f0',
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          py: 3,
        }}
      >
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography
            variant="h4"
            component="h1"
            sx={{
              fontWeight: 'bold',
              color: '#f0f0f0',
              bgcolor: '#1f2937',
              p: 2,
              borderRadius: 2,
            }}
          >
            My League Stats
          </Typography>
        </Box>
        <Grid container spacing={2} sx={{ width: '100%' }}>
          <Grid item xs={12}>
            <Card sx={{ p: 3, bgcolor: '#2d3748', color: '#f0f0f0', boxShadow: 5, borderRadius: 3 }}>
              <SummonerForm onAccountDataFetched={handleAccountDataFetched} />
            </Card>
          </Grid>
          {summoner && (
            <Grid item xs={8}>
              <SummonerProfile summoner={summoner} wins={wins} losses={losses} winRate={winRate} />
            </Grid>
          )}
          {summoner && (
            <Grid container spacing={0} sx={{ width: '100%', m: 0, alignItems: 'stretch' }}>
              <Grid item xs={5} sx={{ p: 0 }}>
                <Box sx={{ height: '100%' }}> {/* Wrap to ensure full height usage */}
                  <ChampionPerformance puuid={summoner.puuid} />
                </Box>
              </Grid>
              <Grid item xs={7} sx={{ p: 0 }}>
                <Card sx={{ p: 2, bgcolor: '#1f2937', color: '#f0f0f0', boxShadow: 5, borderRadius: 3, height: '100%' }}>
                  <MatchList matches={matches} />
                  {summoner && (
                    <LoadMoreMatches
                      puuid={summoner.puuid}
                      limit={limit}
                      matches={matches}
                      setMatches={setMatches}
                      setWins={setWins}
                      setLosses={setLosses}
                      setWinRate={setWinRate}
                    />
                  )}
                </Card>
              </Grid>
            </Grid>
          )}
        </Grid>
      </Container>
    </Box>
  );
}

export default App;
