import React, { useState, useEffect } from 'react';
import { getAccountData, getSummonerData, getMatchHistoryWithWinRate, getVersion } from './services/api';
import SummonerForm from './components/SummonerForm';
import SummonerProfile from './components/SummonerProfile';
import MatchList from './components/MatchList';
import WinRateChart from './components/WinRateChart';
import './App.css';

function App() {
  const [summoner, setSummoner] = useState(null);
  const [matches, setMatches] = useState([]);
  const [version, setVersion] = useState('');
  const [wins, setWins] = useState(0);
  const [losses, setLosses] = useState(0);
  const [winRate, setWinRate] = useState(0);

  useEffect(() => {
    const fetchVersion = async () => {
      const versionData = await getVersion();
      if (versionData) {
        setVersion(versionData.version);
      }
    };
    fetchVersion();
  }, []);

  const handleAccountDataFetched = async (data) => {
    const { puuid, gameName, tagLine } = data;

    const summonerData = await getSummonerData(puuid);
    setSummoner({ ...data, ...summonerData });

    // Fetch match history with win rate
    const matchHistoryData = await getMatchHistoryWithWinRate(puuid);
    if (matchHistoryData.error) {
      setMatches([]);
      setWins(0);
      setLosses(0);
      setWinRate(0);
      return;
    }

    // Set matches and win rate data from the backend
    setMatches(matchHistoryData.matchDetails);
    setWins(matchHistoryData.wins);
    setLosses(matchHistoryData.losses);
    setWinRate(matchHistoryData.winRate);
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Riot Games Data Viewer</h1>
      </header>
      <main className="app-main">
        <div className="app-section">
          <SummonerForm onAccountDataFetched={handleAccountDataFetched} />
        </div>
        <div className="app-section profile-chart">
          <div className="profile-details">
            <img src={summoner?.profileIconUrl || 'fallback-profile-icon.png'} alt="Profile Icon" />
            <div className="profile-text">
              <p><strong>Name:</strong> {summoner?.gameName}#{summoner?.tagLine}</p>
              <p><strong>Level:</strong> {summoner?.summonerLevel}</p>
              <p><strong>Rank:</strong> {summoner?.rank?.tier} {summoner?.rank?.rank || 'Unranked'}</p>
            </div>
          </div>
          <div className="win-rate-chart">
            {matches.length > 0 && <WinRateChart wins={wins} losses={losses} winRate={winRate} />}
          </div>
        </div>
        <div className="app-section match-history">
          <MatchList matches={matches} />
        </div>
      </main>
      <footer className="app-footer">
        <p>&copy; 2024 George Ulloa's Riot API App</p>
      </footer>
    </div>
  );
}

export default App;
