// src/components/ChampionPerformance.js
import React, { useEffect, useState } from 'react';
import { getChampionPerformance } from '../services/api';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography, CircularProgress } from '@mui/material';

function ChampionPerformance({ puuid }) {
  const [championPerformance, setChampionPerformance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPerformanceData = async () => {
      const data = await getChampionPerformance(puuid);
      if (!data.error) {
        setChampionPerformance(data.championPerformance);
      }
      setLoading(false);
    };
    fetchPerformanceData();
  }, [puuid]);

  if (loading) return <CircularProgress />;

  return (
    <TableContainer component={Paper} sx={{ mt: 4 }}>
      <Typography variant="h6" component="div" sx={{ p: 2, textAlign: 'center' }}>Champion Performance</Typography>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Champion</TableCell>
            <TableCell align="right">Games Played</TableCell>
            <TableCell align="right">Win Rate (%)</TableCell>
            <TableCell align="right">Average KDA</TableCell>
            <TableCell align="right">CS/Min</TableCell>
            <TableCell align="right">Gold Earned</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {championPerformance.map((champ) => (
            <TableRow key={champ.champion}>
              <TableCell>{champ.champion}</TableCell>
              <TableCell align="right">{champ.games}</TableCell>
              <TableCell align="right">{champ.win_rate}</TableCell>
              <TableCell align="right">{champ.average_kda}</TableCell>
              <TableCell align="right">{champ.average_cs_per_min}</TableCell>
              <TableCell align="right">{champ.average_gold}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default ChampionPerformance;
