import React from 'react';
import { Doughnut } from 'react-chartjs-2';

const WinRateChart = ({ wins, losses, winRate }) => {
  const data = {
    labels: ['Losses', 'Wins'],
    datasets: [
      {
        data: [losses, wins],
        backgroundColor: ['#e63946', '#4a90e2'], // Red for losses, blue for wins
        borderWidth: 0,
      },
    ],
  };

  const options = {
    cutout: '70%', // Creates a doughnut with a smaller inner circle
    plugins: {
      tooltip: { enabled: false },
      legend: { display: false },
    },
  };

  return (
    <div style={{ position: 'relative', width: '150px', height: '150px' }}>
      <Doughnut data={data} options={options} />
      <div style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        color: '#f0f0f0',
        fontSize: '20px',
        fontWeight: 'bold',
      }}>
        {winRate}%
      </div>
      <div style={{ textAlign: 'center', color: '#f0f0f0', marginTop: '10px' }}>
        {wins + losses}G {wins}W {losses}L
      </div>
    </div>
  );
};

export default WinRateChart;
