import React from 'react';
import { Doughnut } from 'react-chartjs-2';

const WinRateChart = ({ wins, losses, winRate }) => {
  const totalGames = wins + losses;

  const data = {
    labels: ['Losses', 'Wins'],
    datasets: [
      {
        data: [losses, wins],
        backgroundColor: ['#e63946', '#4a90e2'], // Red for losses, blue for wins
        borderWidth: 0,
        hoverOffset: 8, // Add hover effect for a modern touch
      },
    ],
  };

  const options = {
    cutout: '75%', // Creates a doughnut with a larger inner circle
    plugins: {
      tooltip: { enabled: false },
      legend: { display: false },
    },
    animation: {
      animateRotate: true,
      animateScale: true,
    },
  };

  return (
    <div style={{ position: 'relative', width: '180px', height: '180px' }}>
      <Doughnut data={data} options={options} />
      <div style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        color: '#f0f0f0',
        fontSize: '24px',
        fontWeight: 'bold',
        textShadow: '0px 1px 5px rgba(0, 0, 0, 0.3)',
      }}>
        {winRate}%
      </div>
      <div style={{
        textAlign: 'center',
        color: '#f0f0f0',
        marginTop: '15px',
        fontSize: '14px',
        textShadow: '0px 1px 3px rgba(0, 0, 0, 0.3)',
      }}>
        {totalGames} Games | {wins}W {losses}L
      </div>
    </div>
  );
};

export default WinRateChart;
