import React, { useState, useEffect } from 'react';
import { Box, Typography, Avatar, Grid } from '@mui/material';
import { getChampionPerformance } from '../services/api';

function ChampionPerformance({ puuid }) {
  const [championPerformance, setChampionPerformance] = useState([]);

  useEffect(() => {
    const fetchPerformance = async () => {
      const data = await getChampionPerformance(puuid);
      setChampionPerformance(data.championPerformance || []);
    };
    fetchPerformance();
  }, [puuid]);

  return (
    <Box sx={{ mt: 3, bgcolor: '#1f2937', p: 3, borderRadius: 2, boxShadow: 3, width: '100%' }}>
      {championPerformance.map((champ, index) => (
        <Box
          key={index}
          sx={{
            display: 'flex',
            alignItems: 'center',
            p: 1.5,
            mb: 1,
            bgcolor: '#2d3748',
            borderRadius: 2,
          }}
        >
          <Avatar
            src={`https://ddragon.leagueoflegends.com/cdn/14.21.1/img/champion/${champ.champion}.png`}
            alt={champ.champion}
            sx={{ width: 48, height: 48, mr: 2 }}
          />
          <Box sx={{ flex: 1 }}>
            <Typography variant="body1" fontWeight="bold" sx={{ color: '#f0f0f0' }}>
              {champ.champion}
            </Typography>
            <Typography variant="body2" color="#a0aec0">
              CS {champ.average_cs_per_min} ({(champ.average_cs_per_min * champ.games).toFixed(1)})
            </Typography>
          </Box>
          <Box sx={{ textAlign: 'center', minWidth: 80 }}>
            <Typography
              variant="body1"
              sx={{
                fontWeight: 'bold',
                color: champ.average_kda.split(':')[0] >= 3 ? '#4caf50' : '#f44336',
              }}
            >
              {champ.average_kda} KDA
            </Typography>
            <Typography variant="body2" color="#a0aec0">
              {champ.kills.toFixed(1)} / {champ.deaths.toFixed(1)} / {champ.assists.toFixed(1)}
            </Typography>
          </Box>
          <Box sx={{ textAlign: 'center', minWidth: 50 }}>
            <Typography variant="body1" sx={{ color: '#f0f0f0' }}>
              {champ.games} Played
            </Typography>
          </Box>
          <Box sx={{ textAlign: 'center', minWidth: 60 }}>
            <Typography
              variant="body1"
              sx={{
                fontWeight: 'bold',
                color: champ.win_rate >= 50 ? '#4caf50' : '#f44336',
              }}
            >
              {champ.win_rate}% Win
            </Typography>
          </Box>
        </Box>
      ))}
    </Box>
  );
}

export default ChampionPerformance;
