import React, { useState, useEffect } from 'react';
import { Container, Box, Typography, Grid, Card } from '@mui/material';
import { getAccountData, getSummonerData, getVersion } from './services/api';
import SummonerForm from './components/SummonerForm';
import SummonerProfile from './components/SummonerProfile';
import MatchList from './components/MatchList';
import LoadMoreMatches from './components/LoadMoreMatches';

function App() {
  const [summoner, setSummoner] = useState(null);
  const [matches, setMatches] = useState([]);
  const [version, setVersion] = useState('');
  const [wins, setWins] = useState(0);
  const [losses, setLosses] = useState(0);
  const [winRate, setWinRate] = useState(0);
  const [limit] = useState(10);

  useEffect(() => {
    const fetchVersion = async () => {
      const versionData = await getVersion();
      if (versionData) {
        setVersion(versionData.version);
      }
    };
    fetchVersion();
  }, []);

  const handleAccountDataFetched = async (data) => {
    const { puuid, gameName, tagLine } = data;
    const summonerData = await getSummonerData(puuid);
    setSummoner({ ...data, ...summonerData });
    setMatches([]);
  };

  return (
    <Box 
      sx={{ 
        bgcolor: 'linear-gradient(145deg, #1a1c23, #2d2f39)', 
        minHeight: '100vh', 
        display: 'flex', 
        flexDirection: 'column',
        padding: '20px',
        boxShadow: '0px 4px 20px rgba(0, 0, 0, 0.5)',
      }}
    >
      <Container
        maxWidth="md"
        sx={{
          color: '#f0f0f0',
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          py: 3,
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.5)',
        }}
      >
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography
            variant="h4"
            component="h1"
            sx={{
              fontWeight: 'bold',
              color: '#f0f0f0',
              bgcolor: '#1f2937',
              p: 2,
              borderRadius: 2,
              textShadow: '0px 0px 10px rgba(255, 255, 255, 0.5)', // Glow effect
            }}
          >
            My League Stats
          </Typography>
        </Box>
        <Grid container spacing={4} sx={{ width: '100%' }}>
          <Grid item xs={12}>
            <Card 
              sx={{ 
                p: 3, 
                bgcolor: '#2d3748', 
                color: '#f0f0f0', 
                boxShadow: 5, 
                borderRadius: 3,
                transition: 'transform 0.2s ease, box-shadow 0.2s ease',
                '&:hover': {
                  transform: 'scale(1.02)',
                  boxShadow: '0px 8px 15px rgba(0, 0, 0, 0.5)',
                }
              }}
            >
              <SummonerForm onAccountDataFetched={handleAccountDataFetched} />
            </Card>
          </Grid>
          {summoner && (
            <Grid item xs={12}>
              <SummonerProfile 
                summoner={summoner} 
                wins={wins} 
                losses={losses} 
                winRate={winRate} 
              />
            </Grid>
          )}
          <Grid item xs={12}>
            <Card 
              sx={{ 
                p: 3, 
                bgcolor: '#1f2937', 
                color: '#f0f0f0', 
                boxShadow: 5, 
                borderRadius: 3 
              }}
            >
              <MatchList matches={matches} />
              {summoner && (
                <LoadMoreMatches
                  puuid={summoner.puuid}
                  limit={limit}
                  matches={matches}
                  setMatches={setMatches}
                  setWins={setWins}
                  setLosses={setLosses}
                  setWinRate={setWinRate}
                />
              )}
            </Card>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
}

export default App;
