import React from 'react';

function Match({ match }) {
  if (!match) return null;

  const { champion, win, items, kills, gameEndTimestamp, version } = match;
  const matchOutcome = win ? 'Victory' : 'Defeat';
  const outcomeColor = win ? '#76c893' : '#e63946';

  const championImage = (
    <img
      src={`https://ddragon.leagueoflegends.com/cdn/${version}/img/champion/${champion}.png`}
      alt={`${champion}`}
      className="champion-image"
    />
  );

  const itemImages = items
    .filter(itemId => itemId > 0)
    .map((itemId, index) => (
      <img
        key={`${itemId}-${index}`}
        src={`https://ddragon.leagueoflegends.com/cdn/${version}/img/item/${itemId}.png`}
        alt={`Item ${itemId}`}
        className="item-image"
      />
    ));

  return (
    <div className="card match" style={{ borderLeft: `5px solid ${outcomeColor}` }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        {championImage}
        <div>
          <p><strong>Champion:</strong> {champion}</p>
          <p><strong>Outcome:</strong> <span style={{ color: outcomeColor }}>{matchOutcome}</span></p>
          <p><strong>Kills:</strong> {kills}</p>
          <p><strong>Game End Time:</strong> {new Date(gameEndTimestamp).toLocaleString()}</p>
        </div>
      </div>
      <div className="item-images">
        <strong>Items Used:</strong> {itemImages}
      </div>
    </div>
  );
}

export default Match;
