import React from 'react';
import { Avatar } from '@mui/material';

function ChampionImage({ champion, version }) {
  const imageUrl = `https://ddragon.leagueoflegends.com/cdn/${version}/img/champion/${champion}.png`;

  return (
    <Avatar
      src={imageUrl}
      alt={champion}
      sx={{ width: 48, height: 48, borderRadius: '8px' }}
      onError={(e) => (e.target.src = 'fallback-champion-icon.png')}
    />
  );
}

export default ChampionImage;
