import React, { useState } from 'react';
import { TextField, Button, Box, CircularProgress, Typography } from '@mui/material';
import { getAccountData } from '../services/api';

function SummonerForm({ onAccountDataFetched }) {
  const [gameName, setGameName] = useState('');
  const [tagLine, setTagLine] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const data = await getAccountData(gameName, tagLine);
      if (data && !data.error) {
        onAccountDataFetched(data);
      } else {
        setError(data.error || 'Error fetching data');
      }
    } catch (err) {
      setError('Error fetching account data');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ textAlign: 'center', mt: 3, bgcolor: '#2d3748', p: 3, borderRadius: 2 }}>
      <TextField
        label="Game Name"
        variant="outlined"
        value={gameName}
        onChange={(e) => setGameName(e.target.value)}
        required
        sx={{ mr: 2, input: { color: '#f0f0f0' } }}
      />
      <TextField
        label="Tag Line"
        variant="outlined"
        value={tagLine}
        onChange={(e) => setTagLine(e.target.value)}
        required
        sx={{ mr: 2, input: { color: '#f0f0f0' } }}
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={loading}
        sx={{ bgcolor: '#4a90e2' }}
      >
        {loading ? <CircularProgress size={24} color="inherit" /> : 'Get Account Data'}
      </Button>
      {error && (
        <Typography variant="body2" color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      )}
    </Box>
  );
}

export default SummonerForm;
