import React, { useState } from 'react';
import { getAccountData, getSummonerData, getMatchHistory, getMatchData } from './services/api';
import SummonerForm from './components/SummonerForm';
import SummonerProfile from './components/SummonerProfile';
import MatchList from './components/MatchList';

function App() {
  const [summoner, setSummoner] = useState(null);
  const [matches, setMatches] = useState([]);

  const handleAccountDataFetched = async (data) => {
    const { puuid, gameName, tagLine } = data;

    // Fetch summoner data (rank, level)
    const summonerData = await getSummonerData(puuid);
    setSummoner({ ...data, ...summonerData });

    // Fetch match history
    const matchIds = await getMatchHistory(puuid);
    const limitedMatchIds = matchIds.slice(0, 20);

    // Fetch individual match details
    const matchDataArray = await Promise.all(
      limitedMatchIds.map(matchId => getMatchData(matchId, puuid))
    );

    setMatches(matchDataArray);
  };

  return (
    <div className="App" style={{ textAlign: 'center', fontFamily: 'Arial, sans-serif', color: '#f0f0f0' }}>
      <header style={{ backgroundColor: '#282c34', padding: '20px', color: 'white' }}>
        <h1>Riot Games Data Viewer</h1>
      </header>
      <main style={{ marginTop: '20px' }}>
        <SummonerForm onAccountDataFetched={handleAccountDataFetched} />
        <SummonerProfile summoner={summoner} />
        <MatchList matches={matches} />
      </main>
      <footer style={{ marginTop: '50px', padding: '10px', backgroundColor: '#f1f1f1' }}>
        <p>&copy; 2024 George Ulloa's Riot API App</p>
      </footer>
    </div>
  );
}

export default App;
