import React from 'react';
import { Avatar, Box, Typography } from '@mui/material';

const truncateName = (name, maxLength = 10) => {
  return name.length > maxLength ? `${name.slice(0, maxLength)}...` : name;
};

function Team({ participants, version }) {
  return (
    <Box>
      {participants.map((participant, index) => (
        <Box key={`participant-${index}`} display="flex" alignItems="center" mb={0.5}>
          <Avatar
            src={`https://ddragon.leagueoflegends.com/cdn/${version}/img/champion/${participant.championName}.png`}
            alt={participant.championName}
            sx={{ width: 20, height: 20, mr: 0.5 }}
            onError={(e) => (e.target.src = 'fallback-champion-icon.png')}
          />
          <Typography
            variant="caption"
            color="#f0f0f0"
            sx={{
              whiteSpace: 'nowrap',
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              maxWidth: '100px',
            }}
          >
            {truncateName(participant.summonerName)}
          </Typography>
        </Box>
      ))}
    </Box>
  );
}

export default Team;
