import React, { useEffect, useState } from 'react';
import { getVersion } from './services/api';
import SummonerForm from './components/SummonerForm';

function App() {
  const [version, setVersion] = useState('');
  const [accountData, setAccountData] = useState(null);

  useEffect(() => {
    getVersion().then(data => {
      if (data) {
        setVersion(data.version);
      }
    });
  }, []);

  return (
    <div className="App" style={{ textAlign: 'center', fontFamily: 'Arial, sans-serif' }}>
      <header style={{ backgroundColor: '#282c34', padding: '20px', color: 'white' }}>
        <h1>Riot Games Data Viewer</h1>
      </header>
      <main style={{ marginTop: '20px' }}>
        <h2>Latest Riot Games Version:</h2>
        {version ? (
          <p style={{ fontSize: '1.5em', color: '#61dafb' }}>{version}</p>
        ) : (
          <p>Loading...</p>
        )}

        <SummonerForm onAccountDataFetched={(data) => setAccountData(data)} />

        {accountData && (
          <div style={{ marginTop: '20px' }}>
            <h3>Account Data:</h3>
            <pre style={{ textAlign: 'left', display: 'inline-block', background: '#f1f1f1', padding: '10px' }}>
              {JSON.stringify(accountData, null, 2)}
            </pre>
          </div>
        )}
      </main>
      <footer style={{ marginTop: '50px', padding: '10px', backgroundColor: '#f1f1f1' }}>
        <p>&copy; 2024 George Ulloa's Riot API App</p>
      </footer>
    </div>
  );
}

export default App;
